staticBinding.value:
cp /etc/hosts /etc/hosts.orig;
sed -i '/::1/s/^/#/g' /etc/hosts;
chmod 775 -R /tmp;
modprobe br_netfilter;
echo 'net.ipv4.ip_forward = 1' > /etc/sysctl.d/50-default.conf; 
echo 'net.ipv6.ip_forward = 1' >> /etc/sysctl.d/50-default.conf;
echo 'net.ipv6.conf.all.disable_ipv6=0'>> /etc/sysctl.d/50-default.conf; 
echo 'net.ipv6.conf.default.disable_ipv6=0'>> /etc/sysctl.d/50-default.conf;
echo 'net.bridge.bridge-nf-call-iptables = 1' >>/etc/sysctl.d/50-default.conf;
echo 'net.bridge.bridge-nf-call-ip6tables = 1' >>/etc/sysctl.d/50-default.conf;
echo 'net.core.wmem_max=4194304' >> /etc/sysctl.d/50-default.conf;
echo 'net.core.rmem_max=4194304' >> /etc/sysctl.d/50-default.conf;
echo 'net.ipv4.tcp_wmem=4096 87380 4194304' >> /etc/sysctl.d/50-default.conf;
echo 'net.ipv4.tcp_rmem=4096 87380 4194304' >> /etc/sysctl.d/50-default.conf;
echo 'net.ipv4.ip_local_port_range = 1024 65535' >> /etc/sysctl.d/50-default.conf;
sysctl -p:
sysctl --system;

echo 'itsma hard nofile 1000000'>>/etc/security/limits.conf
echo 'itsma soft nofile 1000000' >>/etc/security/limits.conf

systemctl daemon-reload;
swapoff -a

yum -y remove chrony; 
yum -y install chrony;
mkdir -p /var/lib/chrony; 
echo > /var/lib/chrony/drift;
systemctl enable chronyd;
systemctl start chronyd; 
chronyc makestep; 
timedatectl; 
hwclock -w

cp ${installersPath}/${smaxInstallDir}/install.properties.orig ${installersPath}/${smaxInstallDir}/install.properties
cd ${installersPath};
rm-f ${smaxInstallDir};
yum -y remover unzip;
rm -f /etc/sysctl.d/50-default.conf;
sysctl --system;
systemctl daemon-reload;
cp /etc/hosts.orig /etc/hosts;
rm -rf ${k8Path};
