staticBinding.value:
su itsma;
cd /home/itsma;
echo '#!/usr/bin/env bash' > run_archive.sh;
echo ' '  >> run_archive.sh;
echo '# ------------------------------------------------------------' >> run_archive.sh;
echo '# Backup source logs to target log folder' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo 'SRC_FOLDER="${1%/}"' >> run_archive.sh;
echo 'DST_FOLDER="${2%/}"' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo '# -------------------------------------------------------------' >> run_archive.sh;
echo 'if [ -z "$2" ]' >> run_archive.sh;
echo 'then' >> run_archive.sh;
echo 'echo "Usage:"' >> run_archive.sh;
echo 'echo "        ./run_archive.sh source target"' >> run_archive.sh;
echo 'echo "Example:"' >> run_archive.sh;
echo 'echo "        ./run_archive.sh /var/vols/itom/itsma/itsma-itsma-global/logs/ /var/vols/itom/core/archive" >>' run_archive.sh;
echo ' ' >> run_archive.sh; 
echo 'exit 0' >> run_archive.sh;
echo 'fi' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo 'fn_is_valid_path() {' >> run_archive.sh;
echo 'local FOLDER="$1"' >> run_archive.sh;
echo 'if [ "${FOLDER:0:1}" != / ]' >> run_archive.sh;
echo 'then' >> run_archive.sh;
echo 'echo "Failed. Folder $FOLDER needs to be an absolute path"' >> run_archive.sh;
echo 'exit 0' >> run_archive.sh;
echo 'fi' >> run_archive.sh;
echo '}' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo 'fn_is_folder_exists() {' >> run_archive.sh;
echo 'local FOLDER="$1"' >> run_archive.sh;
echo 'if [ ! -d "$FOLDER" ]' >> run_archive.sh;
echo 'then' >> run_archive.sh;
echo 'echo "Failed. Folder $FOLDER doesn't exist. Make sure it's mounted in the system."' >> run_archive.sh;
echo 'exit 0' >> run_archive.sh;
echo 'fi        ' >> run_archive.sh;
echo '}' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo 'fn_folder_writable() {' >> run_archive.sh;
echo 'if [ ! -w  "$1" ]' >> run_archive.sh;
echo 'then' >> run_archive.sh;
echo 'echo "Failed. Folder $1 is not writable."' >> run_archive.sh;
echo 'exit 0' >> run_archive.sh;
echo 'fi' >> run_archive.sh;
echo '}' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo 'fn_valid_parameters() {' >> run_archive.sh;
echo 'fn_is_valid_path $SRC_FOLDER' >> run_archive.sh;
echo 'fn_is_valid_path $DST_FOLDER' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo 'fn_is_folder_exists $SRC_FOLDER' >> run_archive.sh;
echo 'fn_is_folder_exists $DST_FOLDER' >> run_archive.sh;
echo ' ' >> run_archive.sh; 
echo 'fn_folder_writable $DST_FOLDER' >> run_archive.sh;
echo 'fn_folder_writable $SRC_FOLDER' >> run_archive.sh;
echo '}' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo 'fn_archive_to_target_dir() {' >> run_archive.sh;
echo 'local currentTime=`date +%Y-%m-%d-%H-%M-%S`' >> run_archive.sh;
echo 'local baseName=`basename $SRC_FOLDER`' >> run_archive.sh;
echo 'local name="${baseName}-${currentTime}.tar.gz"' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo 'pushd ${SRC_FOLDER}  > /dev/null' >> run_archive.sh;
echo 'echo "Before archiving, the disk space usage of $(pwd) folder is: " $(du -sh)' >> run_archive.sh;
echo 'tar -czf "${DST_FOLDER}/${name}" *' >> run_archive.sh;
echo ' ' >> run_archive.sh;
echo 'if [ -e "${DST_FOLDER}/${name}" ]' >> run_archive.sh;
echo 'then' >> run_archive.sh;
echo' echo "Completed archiving and ompressing logs to ${DST_FOLDER}."' >> run_archive.sh;
echo 'echo "Starting to empty log files and clean non log file under $SRC_FOLDER folder..."' >> run_archive.sh;
echo 'find . -type f -name "*"[log,txt] -not -empty | xargs -i truncate -s 0 {}' >> run_archive.sh;
echo 'echo "After archiving, the disk space usage of $(pwd) folder is: " $(du -sh)' >> run_archive.sh;
echo 'else' >> run_archive.sh;
echo 'echo "The archiving job was NOT run successfully. Please check your configuration."' >> run_archive.sh;
echo 'fi        ' >> run_archive.sh;
echo 'popd > /dev/null' >> run_archive.sh;
echo '}' >> run_archive.sh;
echo 'fn_valid_parameters' >> run_archive.sh;
echo 'fn_archive_to_target_dir' >> run_archive.sh;
echo '#------------------------------------ End of Script -------------------' >> run_archive.sh;
chmod +x run_archive.sh;
