staticBinding.value:
systemctl stop postgresql-9.6;
cd /usr/pgsql-9.6/bin;
./postgresql96-setup initdb;
systemctl start postgresql-9.6;
systemctl enable postgresql-9.6;
netstat -plntu | grep 5432;
sudo -u postgres psql -c "ALTER USER postgres WITH PASSWORD '${pgPostgresUserPwd}';"
systemctl disable firewalld;
systemctl stop firewalld;

cp /var/lib/pgsql/9.6/data/postgresql.conf /var/lib/pgsql/9.6/data/postgresql.conf.orig;
cp /var/lib/pgsql/9.6/data/pg_hba.conf /var/lib/pgsql/9.6/data/pg_hba.conf.orig;
sed -i '/listen_addresses/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i '/wal_level/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i '/synchronous_commit/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i '/archive_mode/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i '/archive_command/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i '/max_wal_senders/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i '/wal_keep_segments/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i '/synchronous_standby_names/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i "s/listen_addresses =.*/listen_addresses ='*'/" /var/lib/pgsql/9.6/data/postgresql.conf; 
sed -i "s/wal_level =.*/wal_level ='hot_standby'/" /var/lib/pgsql/9.6/data/postgresql.conf; 
sed -i "s/synchronous_commit =.*/synchronous_commit ='local'/" /var/lib/pgsql/9.6/data/postgresql.conf; 
sed -i "s/archive_mode =.*/archive_mode ='on'/" /var/lib/pgsql/9.6/data/postgresql.conf;  
sed -i "s/archive_command =.*/archive_command='cp %p \/var\/lib\/pgsql\/9.6\/archive\/%f'/" /var/lib/pgsql/9.6/data/postgresql.conf; 
sed -i "s/max_wal_senders =.*/max_wal_senders='2'/" /var/lib/pgsql/9.6/data/postgresql.conf; 
sed -i "s/wal_keep_segments =.*/wal_keep_segments='10'/" /var/lib/pgsql/9.6/data/postgresql.conf; 
sed -i "s/synchronous_standby_names =.*/synchronous_standby_names='pgslave01'/" /var/lib/pgsql/9.6/data/postgresql.conf;
mkdir -p /var/lib/pgsql/9.6/archive/;
chmod 700 /var/lib/pgsql/9.6/archive/;
chown -R postgres:postgres /var/lib/pgsql/9.6/archive/;
echo 'host    replication     replica          127.0.0.1/24            md5' >>  /var/lib/pgsql/9.6/data/pg_hba.conf;
echo 'host    replication     replica          ${pgMasterIp}/24         trust' >>  /var/lib/pgsql/9.6/data/pg_hba.conf;
echo 'host    replication     replica          ${pgNodeIp}/24         trust' >>  /var/lib/pgsql/9.6/data/pg_hba.conf;
systemctl restart  postgresql-9.6;
systemctl status postgresql-9.6;

cd /
sudo -u postgres psql -c "CREATE USER replica  WITH PASSWORD '${pgPostgresUserPwd}'";
sudo -u postgres psql -c "ALTER ROLE replica REPLICATION;";
chown -r postgres.postgres /var/lib/pgsql/9.6/data
