staticBinding.value:
cp /etc/sudoers /etc/sudoers.orig;
echo 'Defaults:root !requiretty' >> /etc/sudoers;
systemctl disable firewalld; stemctl stop firewalld;
yum -y remove firewalld;
cp /etc/hosts /etc/hosts.orig;
sed -i '/::1/s/^/#/g' /etc/hosts;
chmod 775 -R /tmp;
modprobe br_netfilter;
echo "br_netfilter" > /etc/modules-load.d/br_netfilter.conf;
cp /etc/sysctl.d/50-default.conf /etc/sysctl.d/50-default.conf.orig;
echo 'net.ipv4.ip_forward = 1' > /etc/sysctl.d/50-default.conf; 
echo 'net.ipv6.ip_forward = 1' >> /etc/sysctl.d/50-default.conf;
echo 'net.ipv6.conf.all.disable_ipv6=0'>> /etc/sysctl.d/50-default.conf; 
echo 'net.ipv6.conf.default.disable_ipv6=0'>> /etc/sysctl.d/50-default.conf;
echo 'net.bridge.bridge-nf-call-iptables = 1' >>/etc/sysctl.d/50-default.conf;
echo 'net.bridge.bridge-nf-call-ip6tables = 1' >>/etc/sysctl.d/50-default.conf;
echo 'net.core.wmem_max=4194304' >> /etc/sysctl.d/50-default.conf;
echo 'net.core.rmem_max=4194304' >> /etc/sysctl.d/50-default.conf;
echo 'net.ipv4.tcp_wmem=4096 87380 4194304' >> /etc/sysctl.d/50-default.conf;
echo 'net.ipv4.tcp_rmem=4096 87380 4194304' >> /etc/sysctl.d/50-default.conf;
echo 'net.ipv4.ip_local_port_range = 1024 65535' >> /etc/sysctl.d/50-default.conf;
echo 'kernel.sem=50100 128256000 50100 2560'>> /etc/sysctl.d/5--default.conf;
sysctl -p:
sysctl --system;
cp /etc/security/limits.conf /etc/security/limits.conf.orig;
echo 'itsma hard nofile 1000000'>>/etc/security/limits.conf;
echo 'itsma soft nofile 1000000' >>/etc/security/limits.conf;
cp /etc/ssh/sshd_config /etc/ssh/sshd_config.orig;
echo 'MACs hmac-sha2-256,hmac-sha2-512' >> /etc/ssh/sshd_config;
echo 'Ciphers aes128-cbc,aes192-cbc,aes256-cbc,aes128-ctr,aes192-ctr,aes256-ctr' >> /etc/ssh/sshd_config;
systemctl restart sshd.service;
systemctl daemon-reload;
swapon -a;

yum -y remove chrony; 
yum -y install chrony;
mkdir -p /var/lib/chrony; 
echo > /var/lib/chrony/drift;
sed -i '3 a\server ${ntpIp} iburst' /etc/chrony.conf;
systemctl enable chronyd;
systemctl start chronyd; 
timedatectl;
hwclock -w;

cp ${installersPath}/${smaxInstallDir}/install.properties.orig ${installersPath}/${smaxInstallDir}/install.properties
cp /etc/sysctl.d/50-default.conf.orig /etc/sysctl.d/50-default.conf; 
cp /etc/security/limits.conf.orig /etc/security/limits.conf;
cp /etc/sudoers.orig /etc/sudoers;
rm-fr ${installersPath}/${smaxInstallDir};
yum -y remove device-mapper-libs java-1.8.0-openjdk libseccomp libgcrypt libtool-ltdl net-tools nfs-utils rpcbind systemd-libs unzip httpd-tools conntrack-tools curl lvm2 chrony gunzip;
