staticBinding.value:
systemctl stop postgresql-9.6;
cd /usr/pgsql-9.6/bin;
./postgresql96-setup initdb;
systemctl start postgresql-9.6;
systemctl enable postgresql-9.6;
netstat -plntu | grep 5432;
sudo -u postgres psql -c "ALTER USER postgres WITH PASSWORD '${pgPostgresUserPwd}';"
systemctl disable firewalld;
systemctl stop firewalld;
systemctl stop postgresql-9.6;

cd /var/lib/pgsql/9.6/;
mv data data-backup;
mkdir -p data/;
chmod 700 data/;
chown -R postgres:postgres data/;
su - postgres;
pg_basebackup -h ${pgMasterIp} -U replica -D /var/lib/pgsql/9.6/data;
cp /var/lib/pgsql/9.6/data/postgresql.conf /var/lib/pgsql/9.6/data/postgresql.conf.orig;
sed -i '/listen_addresses/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i "s/listen_addresses =.*/listen_addresses ='*'/" /var/lib/pgsql/9.6/data/postgresql.conf; 
sed -i '/hot_standby/s/^#//g' /var/lib/pgsql/9.6/data/postgresql.conf;
sed -i "s/hot_standby =.*/hot_standby ='on'/" /var/lib/pgsql/9.6/data/postgresql.conf;
cp /var/lib/pgsql/9.6/data/recovery.conf /var/lib/pgsql/9.6/data/recovery.conf.orig;
echo 'standby_mode = 'on'' >  /var/lib/pgsql/9.6/data/recovery.conf;
echo "primary_conninfo = 'host=${pgMasterIp} port=5432 user=replica password=${pgPostgresUserPwd} application_name=pgslave01'" >> /var/lib/pgsql/9.6/data/recovery.conf;
echo "trigger_file = '/tmp/postgresql.trigger.5432'" >> /var/lib/pgsql/9.6/data/recovery.conf;
chmod 600 /var/lib/pgsql/9.6/data/recovery.conf;
